function U = radiative_solution(X,Y,k, dx,dy)

    N1 = length(X); N2 = length(Y);

    U = zeros(N1, N2);
    
    for i=1:N1
        x = X(i);
        for j=1:N2
            y = Y(j);
            rad = sqrt(x^2+y^2);
            if(rad==0)
                u = 0;
            elseif(dx==0 && dy==0)
                u = smoother(x,y,0,0) * fundamental(x,y,k,0,0);
            elseif(dx==1 && dy==0)
                u = fundamental(x,y,k,1,0)*smoother(x,y,0,0) + ...
                    fundamental(x,y,k,0,0) * smoother(x,y,1,0);
            elseif(dx==0 && dy==1)
                u = fundamental(x,y,k,0,1)*smoother(x,y,0,0) + ...
                    fundamental(x,y,k,0,0) * smoother(x,y,0,1);
            elseif(dx==1 && dy==1)
                u = fundamental(x,y,k,1,1)*smoother(x,y,0,0) + ...
                    fundamental(x,y,k,1,0)*smoother(x,y,0,1) + ...
                    fundamental(x,y,k,0,1)*smoother(x,y,1,0) + ...
                    fundamental(x,y,k,0,0)*smoother(x,y,1,1);
            elseif(dx==2 && dy==0)
                u = fundamental(x,y,k,2,0)*smoother(x,y,0,0) + ...
                    2*fundamental(x,y,k,1,0)*smoother(x,y,1,0) + ...
                    fundamental(x,y,k,0,0)*smoother(x,y,2,0);
            elseif(dx==0 && dy==2)
                u = fundamental(x,y,k,0,2)*smoother(x,y,0,0) + ...
                    2*fundamental(x,y,k,0,1)*smoother(x,y,0,1) + ...
                    fundamental(x,y,k,0,0)*smoother(x,y,0,2);
            elseif(dx==3 && dy==0)
                u = fundamental(x,y,k,3,0)*smoother(x,y,0,0) + ...
                    3*fundamental(x,y,k,2,0)*smoother(x,y,1,0) + ...
                    3*fundamental(x,y,k,1,0)*smoother(x,y,2,0) + ...
                    fundamental(x,y,k,0,0)*smoother(x,y,3,0);
            elseif(dx==0 && dy==3)
                u = fundamental(x,y,k,0,3)*smoother(x,y,0,0) + ...
                    3*fundamental(x,y,k,0,2)*smoother(x,y,0,1) + ...
                    3*fundamental(x,y,k,0,1)*smoother(x,y,0,2) + ...
                    fundamental(x,y,k,0,0)*smoother(x,y,0,3);
            elseif(dx==4 && dy==0)
                u = fundamental(x,y,k,4,0)*smoother(x,y,0,0) + ...
                    4*fundamental(x,y,k,3,0)*smoother(x,y,1,0) + ...
                    6*fundamental(x,y,k,2,0)*smoother(x,y,2,0) + ...
                    4*fundamental(x,y,k,1,0)*smoother(x,y,3,0) + ...
                    fundamental(x,y,k,0,0)*smoother(x,y,4,0);
            elseif(dx==0 && dy==4)
                u = fundamental(x,y,k,0,4)*smoother(x,y,0,0) + ...
                    4*fundamental(x,y,k,0,3)*smoother(x,y,0,1) + ...
                    6*fundamental(x,y,k,0,2)*smoother(x,y,0,2) + ...
                    4*fundamental(x,y,k,0,1)*smoother(x,y,0,3) + ...
                    fundamental(x,y,k,0,0)*smoother(x,y,0,4);
            elseif(dx==1 && dy==2)
                u = fundamental(x,y,k,1,2)*smoother(x,y,0,0) + ...
                    fundamental(x,y,k,0,2)*smoother(x,y,1,0) + ...
                    2*fundamental(x,y,k,0,1)*smoother(x,y,1,1) + ...
                    2*fundamental(x,y,k,1,1)*smoother(x,y,0,1) + ...
                    fundamental(x,y,k,1,0)*smoother(x,y,0,2) + ...
                    fundamental(x,y,k,0,0)*smoother(x,y,1,2);
            elseif(dx==2 && dy==1)
                u = fundamental(x,y,k,2,1)*smoother(x,y,0,0) + ...
                    fundamental(x,y,k,2,0)*smoother(x,y,0,1) + ...
                    2*fundamental(x,y,k,1,0)*smoother(x,y,1,1) + ...
                    2*fundamental(x,y,k,1,1)*smoother(x,y,1,0) + ...
                    fundamental(x,y,k,0,1)*smoother(x,y,2,0) + ...
                    fundamental(x,y,k,0,0)*smoother(x,y,2,1);
            elseif(dx==2 && dy==2)
                u = fundamental(x,y,k,2,2)*smoother(x,y,0,0) + ...
                    2*fundamental(x,y,k,1,2)*smoother(x,y,1,0) + ...
                    fundamental(x,y,k,0,2)*smoother(x,y,2,0) + ...
                    2*fundamental(x,y,k,2,1)*smoother(x,y,0,1) + ...
                    4*fundamental(x,y,k,1,1)*smoother(x,y,1,1) + ...
                    2*fundamental(x,y,k,0,1)*smoother(x,y,2,1) + ...
                    fundamental(x,y,k,2,0)*smoother(x,y,0,2) + ...
                    2*fundamental(x,y,k,1,0)*smoother(x,y,1,2) + ...
                    fundamental(x,y,k,0,0)*smoother(x,y,2,2);
            elseif(dx==1 && dy==3)
                u = fundamental(x,y,k,1,3)*smoother(x,y,0,0) + ...
                    3*fundamental(x,y,k,1,2)*smoother(x,y,1,0) + ...
                    fundamental(x,y,k,0,3)*smoother(x,y,1,0) + ...
                    3*fundamental(x,y,k,0,2)*smoother(x,y,1,1) + ...
                    3*fundamental(x,y,k,1,1)*smoother(x,y,0,2) + ...
                    3*fundamental(x,y,k,0,1)*smoother(x,y,1,2) + ...
                    fundamental(x,y,k,1,0)*smoother(x,y,0,3) + ...
                    fundamental(x,y,k,0,0)*smoother(x,y,1,3);
            elseif(dx==3 && dy==1)
                u = fundamental(x,y,k,3,1)*smoother(x,y,0,0) + ...
                    3*fundamental(x,y,k,2,1)*smoother(x,y,0,1) + ...
                    fundamental(x,y,k,3,0)*smoother(x,y,0,1) + ...
                    3*fundamental(x,y,k,2,0)*smoother(x,y,1,1) + ...
                    3*fundamental(x,y,k,1,1)*smoother(x,y,2,0) + ...
                    3*fundamental(x,y,k,1,0)*smoother(x,y,2,1) + ...
                    fundamental(x,y,k,0,1)*smoother(x,y,3,0) + ...
                    fundamental(x,y,k,0,0)*smoother(x,y,3,1);
            end
            U(i,j) = u;
        end
    end
end

function f = smoother(x,y,dx,dy)
    R = 1/2;
    rad = sqrt(x^2+y^2);
    if(rad==0)
        f = 0;
    elseif(rad<R)
        f = eta(x,y,dx,dy);
    elseif(dx==0 && dy==0)
        f = 1;
    else
        f = 0;
    end
end

function Gx = fundamental(x,y,k,dx,dy)
% This is actually the r-derivative of the fundamental solution of the 2D
% Helmholtz equation.
%     G = (1i/4) * besselh(0,k*r)
%     Gx = (dr/dx) * (-k*1i/4) * besselh(1,k*r)
    if(dx==0 && dy==0)
% This is the x-derivative of the fundamental solution. It is the actual
% solution that we will plug into the Helmholtz.
        Gx = -(k*x*besselh(1, 1, k*(x^2 + y^2)^(1/2))*1i)/(4*(x^2 + y^2)^(1/2));
    elseif(dx==1 && dy==0)
        Gx = - (k*besselh(1, 1, k*(x^2 + y^2)^(1/2))*1i)/(4*(x^2 + y^2)^(1/2)) + (k*x*((x*besselh(1, 1, k*(x^2 + y^2)^(1/2)))/(x^2 + y^2) - (k*x*besselh(0, 1, k*(x^2 + y^2)^(1/2)))/(x^2 + y^2)^(1/2))*1i)/(4*(x^2 + y^2)^(1/2)) + (k*x^2*besselh(1, 1, k*(x^2 + y^2)^(1/2))*1i)/(4*(x^2 + y^2)^(3/2));
    elseif(dx==0 && dy==1)
        Gx = (k*x*((y*besselh(1, 1, k*(x^2 + y^2)^(1/2)))/(x^2 + y^2) - (k*y*besselh(0, 1, k*(x^2 + y^2)^(1/2)))/(x^2 + y^2)^(1/2))*1i)/(4*(x^2 + y^2)^(1/2)) + (k*x*y*besselh(1, 1, k*(x^2 + y^2)^(1/2))*1i)/(4*(x^2 + y^2)^(3/2));
    elseif(dx==2 && dy==0)
        Gx = (k*((x*besselh(1, 1, k*(x^2 + y^2)^(1/2)))/(x^2 + y^2) - (k*x*besselh(0, 1, k*(x^2 + y^2)^(1/2)))/(x^2 + y^2)^(1/2))*1i)/(2*(x^2 + y^2)^(1/2)) + (k*x*(besselh(1, 1, k*(x^2 + y^2)^(1/2))/(x^2 + y^2) - (2*x^2*besselh(1, 1, k*(x^2 + y^2)^(1/2)))/(x^2 + y^2)^2 - (x*((x*besselh(1, 1, k*(x^2 + y^2)^(1/2)))/(x^2 + y^2) - (k*x*besselh(0, 1, k*(x^2 + y^2)^(1/2)))/(x^2 + y^2)^(1/2)))/(x^2 + y^2) - (k*besselh(0, 1, k*(x^2 + y^2)^(1/2)))/(x^2 + y^2)^(1/2) + (k^2*x^2*besselh(1, 1, k*(x^2 + y^2)^(1/2)))/(x^2 + y^2) + (k*x^2*besselh(0, 1, k*(x^2 + y^2)^(1/2)))/(x^2 + y^2)^(3/2))*1i)/(4*(x^2 + y^2)^(1/2)) + (k*x*besselh(1, 1, k*(x^2 + y^2)^(1/2))*3i)/(4*(x^2 + y^2)^(3/2)) - (k*x^2*((x*besselh(1, 1, k*(x^2 + y^2)^(1/2)))/(x^2 + y^2) - (k*x*besselh(0, 1, k*(x^2 + y^2)^(1/2)))/(x^2 + y^2)^(1/2))*1i)/(2*(x^2 + y^2)^(3/2)) - (k*x^3*besselh(1, 1, k*(x^2 + y^2)^(1/2))*3i)/(4*(x^2 + y^2)^(5/2));
    elseif(dx==0 && dy==2)
        Gx = (k*x*(besselh(1, 1, k*(x^2 + y^2)^(1/2))/(x^2 + y^2) - (2*y^2*besselh(1, 1, k*(x^2 + y^2)^(1/2)))/(x^2 + y^2)^2 - (k*besselh(0, 1, k*(x^2 + y^2)^(1/2)))/(x^2 + y^2)^(1/2) - (y*((y*besselh(1, 1, k*(x^2 + y^2)^(1/2)))/(x^2 + y^2) - (k*y*besselh(0, 1, k*(x^2 + y^2)^(1/2)))/(x^2 + y^2)^(1/2)))/(x^2 + y^2) + (k^2*y^2*besselh(1, 1, k*(x^2 + y^2)^(1/2)))/(x^2 + y^2) + (k*y^2*besselh(0, 1, k*(x^2 + y^2)^(1/2)))/(x^2 + y^2)^(3/2))*1i)/(4*(x^2 + y^2)^(1/2)) + (k*x*besselh(1, 1, k*(x^2 + y^2)^(1/2))*1i)/(4*(x^2 + y^2)^(3/2)) - (k*x*y^2*besselh(1, 1, k*(x^2 + y^2)^(1/2))*3i)/(4*(x^2 + y^2)^(5/2)) - (k*x*y*((y*besselh(1, 1, k*(x^2 + y^2)^(1/2)))/(x^2 + y^2) - (k*y*besselh(0, 1, k*(x^2 + y^2)^(1/2)))/(x^2 + y^2)^(1/2))*1i)/(2*(x^2 + y^2)^(3/2));
    elseif(dx==1 && dy==1)
        Gx = (k*((y*besselh(1, 1, k*(x^2 + y^2)^(1/2)))/(x^2 + y^2) - (k*y*besselh(0, 1, k*(x^2 + y^2)^(1/2)))/(x^2 + y^2)^(1/2))*1i)/(4*(x^2 + y^2)^(1/2)) - (k*x*((x*((y*besselh(1, 1, k*(x^2 + y^2)^(1/2)))/(x^2 + y^2) - (k*y*besselh(0, 1, k*(x^2 + y^2)^(1/2)))/(x^2 + y^2)^(1/2)))/(x^2 + y^2) + (2*x*y*besselh(1, 1, k*(x^2 + y^2)^(1/2)))/(x^2 + y^2)^2 - (k^2*x*y*besselh(1, 1, k*(x^2 + y^2)^(1/2)))/(x^2 + y^2) - (k*x*y*besselh(0, 1, k*(x^2 + y^2)^(1/2)))/(x^2 + y^2)^(3/2))*1i)/(4*(x^2 + y^2)^(1/2)) + (k*y*besselh(1, 1, k*(x^2 + y^2)^(1/2))*1i)/(4*(x^2 + y^2)^(3/2)) - (k*x^2*((y*besselh(1, 1, k*(x^2 + y^2)^(1/2)))/(x^2 + y^2) - (k*y*besselh(0, 1, k*(x^2 + y^2)^(1/2)))/(x^2 + y^2)^(1/2))*1i)/(4*(x^2 + y^2)^(3/2)) - (k*x^2*y*besselh(1, 1, k*(x^2 + y^2)^(1/2))*3i)/(4*(x^2 + y^2)^(5/2)) - (k*x*y*((x*besselh(1, 1, k*(x^2 + y^2)^(1/2)))/(x^2 + y^2) - (k*x*besselh(0, 1, k*(x^2 + y^2)^(1/2)))/(x^2 + y^2)^(1/2))*1i)/(4*(x^2 + y^2)^(3/2));
    elseif(dx==2 && dy==2)
